    private void b17ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b17ActionPerformed
        if (!Funcionalidades.revisaParentesis(expreStr)|| Funcionalidades.hayMasOperador(expreStr) || Pantalla.equals("SyntaxError")){
           Pantalla.setText("Syntax Error");
        }
        else{
            String posfija= Funcionalidades.infixPosfix(expreStr);
            double resultado= Funcionalidades.calculaResultado(posfija);
            expreStr = Double.toString(resultado);
            Pantalla.setText(expreStr);
        }
    }//GEN-LAST:event_b17ActionPerformed

